function display_off() {
i2cset -y 1 0x3c 0x80 0xAE  # Display OFF (sleep mode)
sleep 0.1
}

function init_display() {
i2cset -y 1 0x3c 0x80 0xA8  # Set Multiplex Ratio
i2cset -y 1 0x3c 0x80 0x3F    # value
}

function display_on() {
i2cset -y 1 0x3c 0x80 0xAF  # Display ON (normal mode)
sleep 0.001
}

function reset_cursor() {
i2cset -y 1 0x3c 0x80 0x21  # set column address
i2cset -y 1 0x3c 0x80 0x00  #   set start address
i2cset -y 1 0x3c 0x80 0x7F  #   set end address (127 max)
i2cset -y 1 0x3c 0x80 0x22  # set page address
i2cset -y 1 0x3c 0x80 0x00  #   set start address
i2cset -y 1 0x3c 0x80 0x07  #   set end address (7 max)
}

display_off
display_on
reset_cursor

# fill screen
for i in $(seq 1024)
do
   i2cset -y 1 0x3c 0x40 0xff
done

reset_cursor

# clear screen
for i in $(seq 1024)
do
   i2cset -y 1 0x3c 0x40 0x0
done

reset_cursor

