      while((I2C1->ISR & I2C_ISR_TXIS) != I2C_ISR_TXIS);
      I2C1->TXDR = 0x80; 
      while((I2C1->ISR & I2C_ISR_TXIS) != I2C_ISR_TXIS);
      I2C1->TXDR = arr_init[arr_ch]; 



  

